class Solution { public function findMedianSortedArrays($nums1, $nums2) { $i = 0; $i1 = 0; $i2 = 0; $l = count($nums1) + count($nums2); $a = array_fill(0, floor(($l/2) + 1), 0); while($i < count($a)) { if($i1 < count($nums1) && $i2 < count($nums2)) { $a[$i++] = $nums1[$i1] < $nums2[$i2] ? $nums1[$i1++] : $nums2[$i2++]; } else if($i1 < count($nums1)) { $a[$i++] = $nums1[$i1++]; } else { $a[$i++] = $nums2[$i2++]; } } if($l % 2 == 1) { return $a[count($a) - 1]; } else { $t = ($a[count($a) - 2] + $a[count($a) - 1]); return $t / 2.00; } } }